    // \u5BF9\u7528\u6237\u8F93\u5165\u6570\u636E\u505A\u81EA\u5B9A\u4E49\u663E\u793A\u8F6C\u6362\uFF0C\u4E0D\u5F71\u54CD\u6700\u7EC8\u6570\u636E
    transformer: Function;
    
    // \u5F53\u4EC0\u4E48\u7684\u65F6\u5019\u89E6\u53D1\uFF0C\u8FD4\u56DEtrue\u6216false
    when: Function | boolean;
    
    // \u5BF9type\u503C\u662Fcheckbox\u3001list\u3001rawList\u3001expand\u5C55\u793A\u7ED9\u7528\u6237\u770B\u5230\u7684\u884C\u6570
    pageSize: number;
    
    // \u66F4\u6539\u9ED8\u8BA4\u7684\u524D\u7F00\u663E\u793A\u9ED8\u8BA4\u662F?
